#Question 4
DNA = ""
print(f"unique characters: {set(DNA)}")
nt_count = {}
total_nt = 0
for nt in set(DNA):
	nt_count[nt] = DNA.count(nt)
	print(f"{nt} count: {nt_count[nt]}")
	total_nt += nt_count[nt]

GC_count = nt_count["G"] + nt_count["C"]
GC_percent = 100* GC_count / total_nt
print(f"GC content: {GC_percent:.2f} %.")

